'''

    Make JSON file from the glyph widths in a font. Currently ignores 600-unit-wide glyphs, because this is specifically written to find widths in Recursive Sans.

    0. You must have FontTools installed (https://github.com/fonttools/fonttools)
    1. In your terminal, 

        python <path>/glyphset-coverage-test.py <path>/<font>.ttf

'''

import os
import argparse
import pprint
import json
from fontTools.ttLib import TTFont
import unicodedata

parser = argparse.ArgumentParser(description='Add version numbering to font name IDs')

parser.add_argument('fonts', nargs="+")

def getUnicodeName(c):
    try:
        return unicodedata.name(c).lower()
    except ValueError:
        return "(no unicode name)"

def makeFile(data, fontPath):
    path = fontPath.replace(".ttf","-missing_unicodes.txt")
    with open(path, 'w') as file:
        file.write(data)
        print('saved to ', str(path)) 

def convertUnicodeIntToHex(codepoint):
    hexString = str(hex(codepoint)).replace('0x','').upper()
    print(hexString)
    hexVal = f"0x{hexString.rjust(4,'0')}"
    return hexVal


def main():
    args = parser.parse_args()

    for font_path in args.fonts:

        glyphWidths = {}

        print("\n-----------------------------------------\n")
        print(font_path)
        ttfont = TTFont(font_path)

        cmap = ttfont.getBestCmap()

        unicodeDiffs = ""

        print("Unicodes missing: ")
        unicodeDiffs += "Unicodes missing:\n"
        for codepoint in unicodesRequired:
            if int(codepoint,16) in cmap:
                pass
            else:
                if codepoint == '0x000D':
                    missing = f"- [ ] `{codepoint}`   carriage return"
                    print(missing)
                    unicodeDiffs += f"{missing}\n"
                else:
                    missing = f"- [ ] `{codepoint}` {chr(int(codepoint,16))} {getUnicodeName(chr(int(codepoint,16)))}"
                    print(missing)
                    unicodeDiffs += f"{missing}\n"


        unicodeDiffs += "\n-----------------------------------------------\n"
        print("Unicodes created beyond GF Latin glyph sets:")
        unicodeDiffs += "Unicodes created beyond GF Latin glyph sets:\n"
        for codepoint in cmap:
            codepointHex = convertUnicodeIntToHex(codepoint)
            if codepointHex in unicodesRequired:
                pass
            else:
                additional = f"âœ… `{codepointHex}` {chr(codepoint)} {getUnicodeName(chr(codepoint))}"
                print(additional)
                unicodeDiffs += f"{additional}\n"
        
        makeFile(unicodeDiffs, font_path)


if __name__ == '__main__':
    main()
